import React from "react";
import "./chefCard.css";
import axios from 'axios';

interface Ichef {
    name: string,
    img: string,
    id: string
}

const ChefCard: React.FC<Ichef> = (props: Ichef) => {
    const data: Ichef = props;
    const handleDeleteChef = async (id: string) => {
        const body = { id }
        const response = await axios.delete(`http://localhost:8000/chefs/delete`, { data: body });
        console.log(response)
    }
    return (
        <div className="chef-card-container">
            <img src={data.img} alt="" />
            <span>{data.name}</span>
            <button className="close-chef-btn" onClick={() => { handleDeleteChef(data.id) }}><img src="./Assets/close-btn.svg" alt="" /></button>
        </div>
    );
}

export default ChefCard;
