import React, {useState} from "react";
import "./restaurantCard.css";
import {IRestaurantCard} from '../../../../../types/interfaces/IRestaurantCard';
import { Link, useNavigate } from "react-router-dom";
import { ICard } from "../../../../../types/interfaces/ICard";
import axios from "axios";

interface  IRestaurant {
  name: string,
  img: string,
  dbid: string,
  filterRestaurants: (id: string) => void,
  chef: string,
  rating:number,
  id: number
  location:string,
  isCloseable: boolean
}

const RestaurantCard: React.FC<ICard> = (props:IRestaurant) => {
    // const data: ICard = props;
    const navigation = useNavigate();

    const handleDeleteRestaurant = async (id: string) => {
      const body = { id: id };
      try {
          const response = await axios.delete(`http://localhost:8000/restaurants/delete`, { data: body });
          props.filterRestaurant(id);
      }
      catch (err) {
          console.log(err);
      }
  }

      return (
                <div className="card-container" onClick={()=>{navigation(`${props.location}${props.id}`)}}>
                    <img id="restaurant-image" src={props.img} alt="" />
                    <span id="restaurant-name">{props.name}</span>
                    <span id="chef-name">{props.chef}</span>
                    <img id="rating-image" src={`./Assets/ratings/${props.rating}-rating.svg`} alt="" />
                    {props.isCloseable&&<button className="close-btn" onClick={()=>{handleDeleteRestaurant(props.dbid)}}><img src="./Assets/close-btn.svg" alt="" /></button>}
                </div>
      )
    }

export default RestaurantCard;
