import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { Istore } from "../../../../types/interfaces/Istore";
import './hero.css';
import SearchHero from "./SearchHero";


const Hero: React.FC = () => {
    const userData = useSelector((state: Istore) => state.user);
    const userName: string | undefined = userData.name;
    return (
        <>
            <div className="hero">
                {userName && <div className="hello-div">
                    <span>
                        Hello {userName}!
                    </span>
                </div>
                }
                <SearchHero />
            </div>

        </>
    );
}

export default Hero;
