import React from 'react';
import Navbar from '../navbar/Navbar';
import axios from 'axios';
import './signUp.css';
import { useState } from 'react';

export default function SignUpPage() {

  const [name, setName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');

  const handleSignUp = async () => {
    const body = {
      firstName: name,
      lastName: lastName,
      email: email,
      userName: userName,
      password: password
    }

    const { data } = await axios.post('http://localhost:8000/users', body)
    console.log(data);
  }

  const inputNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setName(e.target.value)
  }
  const inputLastNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setLastName(e.target.value)
  }
  const inputEmailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEmail(e.target.value)
  }
  const inputUserNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setUserName(e.target.value)
  }
  const inputPasswordChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value)
  }

  return (
    <>
      <Navbar />
      <div id="sign-in-form-container">
        <h1>SIGN UP</h1>
        <div id="inputs-sign-in-container">
          <input type="text" placeholder='First name' onChange={inputNameChange} />
          <input type="text" placeholder='Last name' onChange={inputLastNameChange} />
          <input type="email" placeholder="Email address" onChange={inputEmailChange} />
          <input type="text" placeholder="User name" onChange={inputUserNameChange} />
          <input type="password" placeholder="Password" onChange={inputPasswordChange} />
        </div>
        <button className='sign-up-in-page' id="sign-up-btn" onClick={handleSignUp}>SIGN UP</button>
      </div>
    </>
  )
}
