import { createSlice } from "@reduxjs/toolkit";
import axios, { all } from 'axios';
import IRestaurantCard from "../../types/interfaces/IRestaurantCard";
// import finalData from "../../dataFinal.json"

// fetch

const getRestaurantsList = async () => {
    try {
        const response = await fetch('http://localhost:8000/restaurants', { method: 'GET' })
        const data = await response.json()
        // console.log(data);
        return data;
    }
    catch (err) {
        console.log(err);
    }
}
const restaurants = getRestaurantsList().then((data) => console.log(data));

// const getRestaurants = async () => {
//     const { data } = await axios.get('http://localhost:8000/restaurants')
//     return data;
// }

// const data = getRestaurants()
// .then((response)=>response.json())

// const finalData = allRestaurants();
// console.log(finalData);
console.log('out data>>>', getRestaurants());

export const restaurantsSlice = createSlice({
    name: "restaurants",
    initialState: {
        value: finalData,
        chefOfTheWeek: 3
    },
    reducers: {
        filterRestaurants: (state, action) => {
            const filterType = action.payload;
            switch (filterType) {
                case "all":
                    state.value = finalData;
                    break;
                case "new":
                    state.value = finalData;
                    state.value = state.value.filter(
                        (restaurant: IRestaurantCard) => restaurant.isNew === true
                    );
                    break;
                case "most popular":
                    state.value = finalData;
                    state.value = state.value.filter(
                        (restaurant: IRestaurantCard) => restaurant.isPopular === true
                    );
                    break;
                case "open now":
                    let timeNow: Date = new Date;
                    const currentHour = timeNow.getHours();
                    state.value = finalData;
                    state.value = state.value.filter(
                        (restaurant: IRestaurantCard) => restaurant.openHour <= currentHour && restaurant.closeHour > currentHour
                    );
                    break;
                case "chef of the week":
                    state.value = finalData;
                    state.value = state.value.filter(
                        (restaurant: IRestaurantCard) => restaurant.chefID == state.chefOfTheWeek
                    );
                    break;
                default:
                    state.value = state.value;
            }
        }

    }
});

export const { filterRestaurants } = restaurantsSlice.actions;
export default restaurantsSlice.reducer;
