import React, { useEffect, useState } from "react";
import IDishOrder from "../../types/interfaces/mainInterfaces/IDishOrder";
import { useSelector } from "react-redux";
import './cart.css';
import { Istore } from "../../types/interfaces/Istore";

export const Cart: React.FC = () => {
    const [orderList, setOrderList] = useState<IDishOrder[]>([]);
    const orderData = useSelector((state: Istore) => state.order.order);
    useEffect(() => {
        setOrderList(orderData);
        console.log(orderList);
    }, []);

    return (
        <div className="order-cart-container">
            <h2 className="cart-title">Your Order</h2>
            <span className="restaurant-name-cart">restaurant name</span>
            <div className="order-dishes-list-container">
                {orderData.map((item, index) =>
                (
                    <div className="single-dish-order">
                        <img className="dish-cart-image" src={item.img} alt="" />
                        <div>
                            <span>detail</span>
                        </div>
                    </div>
                )
                )}
            </div>

        </div>
    )
}

export default Cart;
