import React from "react";
import "./chefCard.css";
import axios from 'axios';

interface IChefCard {
    name: string,
    img: string,
    id: string,
    filterChefs: (id: string) => void
}

const ChefCard: React.FC<IChefCard> = (props: IChefCard) => {

    const token = sessionStorage.getItem('token');
    const parsedToken = token ? JSON.parse(token) : '';
    console.log(parsedToken.token);
    const axiosInstance = axios.create({
        baseURL: 'http://localhost:8000',
        headers: {
            Authorization: `Bearer ${parsedToken.token}`, // include the JWT token in the Authorization header
        },
    });

    const handleDeleteChef = async (id: string) => {
        const body = { id: id };
        try {
            // const response = 
            // await axios.delete(`http://localhost:8000/chefs/delete`, { data: body });
            await axiosInstance.delete('/chefs/delete');
            props.filterChefs(id);
        }
        catch (err) {
            console.log(err);
        }
    }
    return (
        <div className="chef-card-container">
            <img src={props.img} alt="" />
            <span>{props.name}</span>
            <button className="close-chef-btn" onClick={() => { handleDeleteChef(props.id) }}><img src="./Assets/close-btn.svg" alt="" /></button>
        </div>
    );
}

export default ChefCard;
